var sortedArrayToBST = function (nums) {
  // 将有序数组转换为二叉搜索树
  if (nums.length === 0) return null;
  // 找到中间的元素作为根节点
  const mid = Math.floor(nums.length / 2);
  const root = new TreeNode(nums[mid]);
